const fs = require('fs');
const path = require('path');

// Create a simple 100x100 red square test image
const width = 100;
const height = 100;

// Create a simple BMP file (easier than JPEG)
// BMP header
const fileSize = 54 + width * height * 3; // Header + pixel data
const buffer = Buffer.alloc(fileSize);

// BMP File Header
buffer.write('BM', 0); // Signature
buffer.writeUInt32LE(fileSize, 2); // File size
buffer.writeUInt32LE(0, 6); // Reserved
buffer.writeUInt32LE(54, 10); // Pixel data offset

// DIB Header
buffer.writeUInt32LE(40, 14); // Header size
buffer.writeInt32LE(width, 18); // Width
buffer.writeInt32LE(height, 22); // Height
buffer.writeUInt16LE(1, 26); // Planes
buffer.writeUInt16LE(24, 28); // Bits per pixel
buffer.writeUInt32LE(0, 30); // Compression
buffer.writeUInt32LE(width * height * 3, 34); // Image size
buffer.writeInt32LE(2835, 38); // X pixels per meter
buffer.writeInt32LE(2835, 42); // Y pixels per meter
buffer.writeUInt32LE(0, 46); // Colors used
buffer.writeUInt32LE(0, 50); // Important colors

// Pixel data (BGR format, bottom to top)
let offset = 54;
for (let y = height - 1; y >= 0; y--) {
  for (let x = 0; x < width; x++) {
    buffer[offset++] = 0; // Blue
    buffer[offset++] = 0; // Green
    buffer[offset++] = 255; // Red
  }
}

// Save as BMP first
fs.writeFileSync(path.join(__dirname, 'test-image.bmp'), buffer);

// Also create a simple data URL JPEG for convenience
const jpegDataUrl = 'data:image/jpeg;base64,';
const base64Data = jpegDataUrl.split(',')[1];
const jpegBuffer = Buffer.from(base64Data, 'base64');

fs.writeFileSync(path.join(__dirname, 'test-image.jpg'), jpegBuffer);

console.log('Test images created successfully!');